import React,{Component} from 'react';
import axios from 'axios';
import ImageResults from "../imageResults/imageResults";

class Search extends Component{
    state={
        searchText:'',
        apiUrl:'https://pixabay.com/api',
        apiKey:'22218786-ea1f20e1c5dc0b941cb486cad',
        images:[],
        visible:10
    };
    onTextChange=(e)=>{
        const val=e.target.value;
        this.setState({[e.target.name]:val},()=>{
            if(val==='')
            {
                this.setState({images:[]});
            }
            else{
            axios
            .get(
                `${this.state.apiUrl}/?key=${this.state.apiKey}&q=${
                    this.state.searchText
                }&image_type=photo&safesearch=true&per_page=20`
            )
            .then(res=>this.setState({images:res.data.hits}))
            .catch(err=>console.log(err));
            }
        });
    };
    
    render(){
        console.log(this.state.images.slice(0,10).map((item)=>{
            
        }));
        return(
            <div>
               
            <input type="text" 
            style=
            {{backgroundColor:'black',
             color:'white',
            marginLeft:570,
            marginTop:100,
            paddingTop:20,
            paddingLeft:70,
            fontSize:30,
            borderTopStyle:"hidden",
            borderRightStyle:"hidden",
            borderLeftStyle:"hidden",
            outline:"none",
            borderBottomStyle:"groove",

        
        }}
        placeholder="Search for images 🔍"
        name="searchText"
        
        value={this.state.searchText}
        onChange={this.onTextChange}
             />
<br />
{this.state.images.length>0?(<ImageResults images={this.state.images}/>): <h4>Search Images </h4>}
